import { findAllByDisplayValue } from "@testing-library/react";
import React, { Fragment,useEffect,useState } from "react";
import { Button, Container, Form, FormGroup, Input } from "reactstrap";

const AddEmployee= () =>{

    useEffect( () => {
        document.title="Add Employee";
    },[]);
    
    const [employee,setEmployee] = usestate({});
    //form handler function
    const handleForm=(e)=> {
        console.log(employee);

        e.preventDefault();
    }


    return(
        <Fragment>
            <h1 className="text-center my-3">Fill the Employee Details</h1>
            <Form onSubmit={handleForm}> 
                <FormGroup>
                    <label for="employeeNo"><b>Employee No</b></label>
                        <Input type="text" maxLength="10" name="employeeNo" id="employeeNo" placeholder="Employee No" 
                            onChange={(e)=>{
                            setEmployee({...employee, employeeNo : e.target.value })
                        }}/>
                </FormGroup>
                <FormGroup>
                    <label for="employeeName"><b>Employee Name</b></label>
                        <Input type="text" maxLength="100" name="employeeName" id="employeeName" placeholder="Employee Name" 
                        onChange = {
                            (e)=>{ setEmployee(
                                {
                                    ...employee, employeeName : e.target.value
                                }
                            )}
                        }/>
                </FormGroup>
                <FormGroup>
                    <label for="dateOfJoining"><b>Date of Joining</b></label>
                        <Input type="date" maxLength="10" name="dateOfJoining" id="dateOfJoining" placeholder="Date Of Joining" 
                        onChange = {
                            (e)=>{ setEmployee(
                                {
                                    ...employee, dateOfJoining : e.target.value
                                }
                            )}
                        }/>
                </FormGroup>
                <FormGroup>
                    <label for="department"><b>Department</b></label>
                        <Input type="select" id="department" name="department" maxLength="2" 
                        onChange = {
                            (e)=>{ setEmployee(
                                {
                                    ...employee, department : e.target.value
                                }
                            )}
                        }>
                            <option value="AD">Administration</option>
                            <option value="IT">Inforamtion Technology</option>
                            <option value="HD">Help Desk</option>
                            <option value="HR">Human Resource</option>
                            <option value="OP">Operation</option>
                        </Input>
                </FormGroup>
                <FormGroup>
                    <label for="salary"><b>Salary</b></label>
                        <Input type="numer" name="salary" id="salary" placeholder="salary" 
                        onChange = {
                            (e)=>{ setEmployee(
                                {
                                    ...employee, salary : e.target.value
                                }
                            )}
                        }/>
                </FormGroup>
                <Container className="text-center my-5">
                    <Button type="submit" color="success mr-3">save</Button>&nbsp;
                    <Button color="warning ml-10">clear</Button>

                </Container>
            </Form>
        </Fragment>
    );
};



export default AddEmployee;
